-- Function to get the CFrame from the name of the item
local function getCFrame(itemName)
    local cframeData = ogcframes[itemName]
    if cframeData then
        local position = Vector3.new(cframeData[1], cframeData[2], cframeData[3])
        local rotation = CFrame.new(
            cframeData[4], cframeData[5], cframeData[6],
            cframeData[7], cframeData[8], cframeData[9],
            cframeData[10], cframeData[11], cframeData[12]
        )
        return CFrame.new(position) * rotation
    else
        return nil -- Item not found
    end
end





local function antiSpam()
    local charset = {"£", "$", "%", "^", "&", "*", "-", "_", "+", "=", "!", "@", ";", "~","(", ")", "`", "\\", "|", "[", "]", "{", "}", ":", "'", ".", ",", "<", ">","||||||||||||||||||||||||"}
    local f = ""
    for i=1,10 do
        f = f .. charset[math.random(1, #charset)]
    end
    return f
end

